import React from 'react';
import DonationForm from '../components/donations/DonationForm';

const Donations: React.FC = () => {
  return (
    <div className="container mx-auto px-4">
      <div className="max-w-2xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">Support Our Shelters</h1>
          <p className="text-gray-600">
            Your donation helps us provide care, food, and medical attention to pets
            in need. Every contribution makes a difference in their lives.
          </p>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-6">
            Make a Donation
          </h2>
          
          <DonationForm onSuccess={() => {
            // Handle successful donation
            alert('Thank you for your donation!');
          }} />
        </div>

        <div className="mt-8 bg-purple-50 p-6 rounded-lg">
          <h2 className="text-xl font-semibold text-purple-800 mb-4">
            Why Donate?
          </h2>
          <ul className="space-y-3 text-gray-600">
            <li>• Provide food and medical care for pets in need</li>
            <li>• Support shelter operations and maintenance</li>
            <li>• Help pets find their forever homes</li>
            <li>• Contribute to community welfare</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Donations;
