import React from 'react';
import { Link } from 'react-router-dom';

interface Shelter {
  id: string;
  name: string;
  address: string;
  phone: string;
  email: string;
  description: string;
  imageUrl: string;
  capacity: number;
  currentPets: number;
}

interface ShelterCardProps {
  shelter: Shelter;
}

const ShelterCard: React.FC<ShelterCardProps> = ({ shelter }) => {
  const occupancyPercentage = (shelter.currentPets / shelter.capacity) * 100;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
      <div className="relative h-48">
        <img
          src={shelter.imageUrl}
          alt={shelter.name}
          className="w-full h-full object-cover"
        />
        <div className="absolute top-2 right-2 bg-purple-600 text-white px-2 py-1 rounded-full text-sm">
          {occupancyPercentage.toFixed(1)}% Occupied
        </div>
      </div>
      
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{shelter.name}</h3>
        
        <div className="space-y-2">
          <p className="text-gray-600">
            <span className="font-medium">Address:</span> {shelter.address}
          </p>
          <p className="text-gray-600">
            <span className="font-medium">Phone:</span> {shelter.phone}
          </p>
          <p className="text-gray-600">
            <span className="font-medium">Email:</span> {shelter.email}
          </p>
          <p className="text-gray-600">
            <span className="font-medium">Capacity:</span> {shelter.currentPets}/{shelter.capacity} pets
          </p>
        </div>
        
        <p className="text-gray-700 mt-4 line-clamp-2">{shelter.description}</p>
        
        <div className="mt-4 flex justify-between items-center">
          <Link
            to={`/shelters/${shelter.id}`}
            className="text-purple-600 hover:text-purple-700 font-medium"
          >
            View Details
          </Link>
          <Link
            to={`/shelters/${shelter.id}/pets`}
            className="text-sm text-gray-500 hover:text-gray-700"
          >
            View Pets
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ShelterCard;
