import React, { useState } from 'react';
import ShelterList from '../components/shelters/ShelterList';

const Shelters: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');

  return (
    <div className="container mx-auto px-4">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Pet Shelters</h1>
        
        <div className="mb-6">
          <input
            type="text"
            placeholder="Search shelters..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-600"
          />
        </div>

        <div className="bg-purple-50 p-4 rounded-lg mb-6">
          <h2 className="text-lg font-semibold text-purple-800 mb-2">
            Find a Shelter Near You
          </h2>
          <p className="text-gray-600">
            Browse through our network of trusted pet shelters. Each shelter provides
            a safe and caring environment for pets waiting to find their forever homes.
          </p>
        </div>
      </div>

      <ShelterList />
    </div>
  );
};

export default Shelters;
