import { configureStore } from '@reduxjs/toolkit';
import authReducer, { AuthState } from './slices/authSlice';  // Import AuthState
import petsReducer, { PetsState } from './slices/petsSlice';
import sheltersReducer, { SheltersState } from './slices/sheltersSlice';

export const store = configureStore({
  reducer: {
    auth: authReducer,
    pets: petsReducer,
    shelters: sheltersReducer,
  },
});

export type RootState = {
  auth: AuthState;  // Use the imported AuthState type
  pets: PetsState;  // Make sure PetsState and SheltersState are imported similarly
  shelters: SheltersState;
};

export type AppDispatch = typeof store.dispatch;
