import React from 'react';
import { Link } from 'react-router-dom';

interface Pet {
  id: string;
  name: string;
  type: string;
  breed: string;
  age: number;
  description: string;
  imageUrl: string;
  shelterId: string;
}

interface PetCardProps {
  pet: Pet;
}

const PetCard: React.FC<PetCardProps> = ({ pet }) => {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
      <div className="relative h-48">
        <img
          src={pet.imageUrl}
          alt={pet.name}
          className="w-full h-full object-cover"
        />
        <div className="absolute top-2 right-2 bg-purple-600 text-white px-2 py-1 rounded-full text-sm">
          {pet.type}
        </div>
      </div>
      
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{pet.name}</h3>
        <div className="space-y-2">
          <p className="text-gray-600">
            <span className="font-medium">Breed:</span> {pet.breed}
          </p>
          <p className="text-gray-600">
            <span className="font-medium">Age:</span> {pet.age} years
          </p>
        </div>
        
        <p className="text-gray-700 mt-4 line-clamp-2">{pet.description}</p>
        
        <div className="mt-4 flex justify-between items-center">
          <Link
            to={`/pets/${pet.id}`}
            className="text-purple-600 hover:text-purple-700 font-medium"
          >
            View Details
          </Link>
          <Link
            to={`/shelters/${pet.shelterId}`}
            className="text-sm text-gray-500 hover:text-gray-700"
          >
            View Shelter
          </Link>
        </div>
      </div>
    </div>
  );
};

export default PetCard;
